import { Pipe, PipeTransform } from "@angular/core";

@Pipe({
    name: 'autoCompleteFilter'
})
export class AutoCompleteFilterPipe implements PipeTransform{
    transform(items: Array<any>, searchTerm: string, labelKey?: string) {
        if (!items || !searchTerm) {
            return items;
        }

        return items.filter(i => i[labelKey || 'label'].toLowerCase().includes(searchTerm.toLowerCase()) === true);
    }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'emailMask',
})
export class EmailMaskPipe implements PipeTransform {
    transform(value: string): any {
        if (value) {
            const emailParts = value.split('@');

            if (emailParts.length === 2) {
                const parts = emailParts[0].split('');
                return `${parts[0]}****${parts[parts.length - 1]}@${emailParts[1]}`;
            }
        }

        return value;
    }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'mask',
})
export class MaskPipe implements PipeTransform {
    transform(value: string, numberOfLastCharactersToDisplay: number, email: boolean = false): any {
        if (value) {
            if (!email && value.length >= numberOfLastCharactersToDisplay) {
                return `****${value.substr(value.length - numberOfLastCharactersToDisplay)}`;
            } else if (email) {
                return `****${value.substr(value.indexOf('@'))}`;
            }
        }
        return value;
    }
}

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
    name: 'textTransform',
})
export class TextTransformPipe implements PipeTransform {
    transform(value: string): any {
        if (value) {
            return value.replace(/([A-Z])/g, ' $1').trim();
        }
        return value;
    }
}

