import { Injectable } from '@angular/core';
import { from } from 'rxjs';
import { delay, first } from 'rxjs/operators';

import { User } from '../types/user';
import { DataStoreService } from './data-store.service';
import { TokenService } from './token.service';
import { AuthState } from './auth.state';
import { ConfigService } from './config.service';
import { IConfiguration } from '../types/configuration';

@Injectable({
    providedIn: 'root',
})
export class AuthService {
    private _user: User;
    private _token: string;
    private _refresh: string;
    private _expires: any;
    private _configuration: IConfiguration;

    public get user(): User {
        return this._user;
    }

    constructor(
        private dataStoreService: DataStoreService,
        private tokenService: TokenService,
        private configService: ConfigService
    ) {}

    public async login(mode: string, isoCode: string, username: string, password: string): Promise<void> {
        const userToken = await this.tokenService.signin(mode, isoCode, username, password);

        if (userToken && userToken.token && userToken.refresh) {
            this._token = userToken.token;
            this._refresh = userToken.refresh;

            await this.setToken(userToken);

            await this.afterLoginOrRefreshOrVerification();
        }
    }

    public async logout(): Promise<void> {
        await this.tokenService.xsrf();
        await this.tokenService.revoke();
        this.clearToken();
    }

    public async initialize(): Promise<void> {
        this._configuration = this.configService.getConfiguration();
        this._user = { roles: [], authenticated: false };

        const userToken: any = await this.dataStoreService.get('user-token', false);
        if (userToken && userToken.token && userToken.refresh) {
            this._token = userToken.token;
            this._refresh = userToken.refresh;

            if (await this.verify()) {
                await this.afterLoginOrRefreshOrVerification();
            }
        }
    }

    private async clearToken(): Promise<void> {
        this._token = null;
        this._refresh = null;
        this._expires = null;
        this._user = { roles: [], authenticated: false };

        return await this.dataStoreService.push('user-token', null, false);
    }

    private async setToken(token: string): Promise<void> {
        return await this.dataStoreService.push('user-token', token, false);
    }

    private async afterLoginOrRefreshOrVerification(): Promise<void> {
        try {
            await this.validateTokenValues();
            await this.setTokenData();
            await this.setRefreshTokenTimer();
        } catch {
            this.clearToken();
        }
    }

    private async verify(): Promise<boolean> {
        let valid = true;
        try {
            await this.tokenService.xsrf();
            await this.tokenService.verify(this._token);
        } catch {
            try {
                await this.refreshToken();
            } catch {
                valid = false;
            }
        }
        if (!valid) {
            await this.clearToken();
        }
        return valid;
    }

    public async refreshToken(): Promise<void> {
        await this.tokenService.xsrf();
        const token = await this.tokenService.refresh();

        if (token && token.token && token.refresh) {
            this._refresh = token.refresh;
            this._token = token.token;

            await this.setToken(token);
        }
    }

    private async validateTokenValues(): Promise<void> {
        return new Promise((resolve, reject) => {
            try {
                const tokenInfo = JSON.parse(atob(this._token.split('.')[1]));
                if (
                    tokenInfo.aud.includes(this._configuration.token.aud) &&
                    tokenInfo.iss.includes(this._configuration.token.iss)
                ) {
                    const now = Date.now();
                    if (Date.now() >= Number(tokenInfo.exp) * 1000) {
                        reject();
                    } else {
                        resolve();
                    }
                }
                reject();
            } catch {
                reject();
            }
        });
    }

    private async setTokenData(): Promise<void> {
        const tokenInfo = JSON.parse(atob(this._token.split('.')[1]));

        this._user = {
            authenticated: true,
            country: tokenInfo.country,
            emailId: tokenInfo.emailId,
            roles: tokenInfo.roles,
            firstName: tokenInfo.firstName,
            lastName: tokenInfo.lastName,
        };

        this._expires = Number(tokenInfo.exp);

        return Promise.resolve();
    }

    private async setRefreshTokenTimer(): Promise<void> {
        let value = (Number(this._expires) - Number(this._configuration.token.offset)) * 1000;
        from([0])
            .pipe(delay(new Date(value)), first())
            .subscribe(async (_) => {
                AuthState.Refreshing = true;
                await this.refreshToken();
                await this.afterLoginOrRefreshOrVerification();
            });
        AuthState.Refreshing = false;
    }
}

import { Injectable } from '@angular/core';

@Injectable({
    providedIn: 'root',
})
export class AuthState {
    private static _refreshing: boolean = false;

    public static get Refreshing(): boolean {
        return AuthState._refreshing;
    }

    public static set Refreshing(value: boolean) {
        AuthState._refreshing = value;
    }
}

import { isPlatformBrowser } from '@angular/common';
import { ClassProvider, FactoryProvider, InjectionToken, PLATFORM_ID, Injectable } from '@angular/core';

/* Create a new injection token for injecting the window into a component. */
export const WINDOW = new InjectionToken('WindowToken');

/* Define abstract class for obtaining reference to the global window object. */
export abstract class WindowRef {
    get nativeWindow(): Window | Object {
        throw new Error('Not implemented.');
    }
}

/* Define class that implements the abstract class and returns the native window object. */
@Injectable()
export class BrowserWindowRef extends WindowRef {
    constructor() {
        super();
    }

    get nativeWindow(): Window | Object {
        return window;
    }
}

/* Create an factory function that returns the native window object. */
export function windowFactory(browserWindowRef: BrowserWindowRef, platformId: Object): Window | Object {
    if (isPlatformBrowser(platformId)) {
        return browserWindowRef.nativeWindow;
    }
    return new Object();
}

/* Create a injectable provider for the WindowRef token that uses the BrowserWindowRef class. */
const browserWindowProvider: ClassProvider = {
    provide: WindowRef,
    useClass: BrowserWindowRef,
};

/* Create an injectable provider that uses the windowFactory function for returning the native window object. */
const windowProvider: FactoryProvider = {
    provide: WINDOW,
    useFactory: windowFactory,
    deps: [WindowRef, PLATFORM_ID],
};

/* Create an array of providers. */
export const WINDOW_PROVIDERS = [browserWindowProvider, windowProvider];

import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { Address, AvailableSlot, ShoppingCartStateService, ShoppingCart, DataStoreService } from '@common/src/public-api';

@Injectable({
    providedIn: 'root',
})
export class CheckoutStateService {
    private _addresses: Array<Address> = [];
    private _selectedAddressIndex: number = -1;
    private _selectedSlot: AvailableSlot;
    private _shoppingCart: ShoppingCart;

    public selectedDeliveryOption: string;
    public addressStateChange: BehaviorSubject<boolean> = new BehaviorSubject(true);

    constructor (
        private shoppingCartStateService: ShoppingCartStateService,
        private dataStoreService: DataStoreService
    ) {
    }

    get addresses(): Array<Address> {
        return this._addresses;
    }

    get hasAddresses(): boolean {
        return this._addresses && this._addresses.length > 0;
    }

    get selectedAddress(): Address {
        return this._selectedAddressIndex !== -1 ? this._addresses[this._selectedAddressIndex] : null;
    }

    get currentOrderValue(): number {
        return 0;
    }

    get items(): Array<{title: string, quantity: number, totalPrice: string, price: string, country: string, imageSrc: string}> {
        const shoppingCart = this._shoppingCart;

        if (shoppingCart && shoppingCart.items) {
            return shoppingCart.items.map(item => {
                let src = '';
                const image = item.images.find(i => Number(i.size) === 150);
                if (image) {
                    src = image.src;
                }

                return { 
                    title: item.description,
                    quantity: item.quantity,
                    totalPrice: (item.quantity * item.pricing.retail).toFixed(2),
                    price: item.pricing.retail.toFixed(2),
                    country: item.country,
                    imageSrc: src
                };
            });
        }

        return null;
    }

    get selectedSlot(): AvailableSlot {
        return this._selectedSlot;
    }

    set selectedSlot(slot: AvailableSlot) {
        this._selectedSlot = slot;
    }

    async setSelectedAddress(): Promise<void> {
        const defaultAddressIndex = this._addresses.findIndex((a) => a.isDefaultAddress === true);
        this._selectedAddressIndex = defaultAddressIndex === -1 ? 0 : defaultAddressIndex;
        return Promise.resolve();
    }

    async saveState(): Promise<void> {
        await this.dataStoreService.push('checkout-state', { 
            addresses: this._addresses,
            selectedAddressIndex: this._selectedAddressIndex,
            selectedSlot: this._selectedSlot,
            shoppingCart: this.shoppingCartStateService.shoppingCart
        });
    }

    async clearState(): Promise<void> {
        await this.dataStoreService.push('checkout-state', null);
    }

    async setState(): Promise<void> {
        const state = await this.dataStoreService.get('checkout-state');

        if (state) {
            this._addresses = state.addresses;
            this._selectedAddressIndex = state.selectedAddressIndex;
            this._selectedSlot = state.selectedSlot;
            this._shoppingCart = state.shoppingCart;
        }
    }

    setSelectedAddressIndex(index: number): void {
        this._selectedAddressIndex = index;
    }

    removeAddress(id: string): void {
        const index = this._addresses.findIndex((a) => a._id === id);

        this._addresses.splice(index, 1);
    }

    addAddress(address: Address): number {
        let index: number = -1;

        if (!!address._id) {
            index = this._addresses.findIndex((a) => a._id === address._id);
        }

        if (index === -1) {
            if (address.isDefaultAddress === true) {
                this._addresses.splice(0, 0, address);
                index = 0;
            } else {
                this._addresses.push(address);
                index = this._addresses.length - 1;
            }
        }

        if (this._addresses.length === 1) {
            this._addresses[0].isDefaultAddress = true;
        } else if (address.isDefaultAddress === true) {
            this._addresses.forEach((a, i) => {
                if (i !== 0) {
                    a.isDefaultAddress = false;
                }
            });
        }

        return index;
    }

    clearShoppingCart(): void {
        this.shoppingCartStateService.reset();
    }
}

import { Injectable } from '@angular/core';
