@Injectable({
    providedIn: 'root',
})
export class MenuStateService {
    private _mainMenuKey: BehaviorSubject<string> = new BehaviorSubject(null);
    private _subMenuKey: BehaviorSubject<string> = new BehaviorSubject(null);
    private _showSideBar: BehaviorSubject<boolean> = new BehaviorSubject(false);
    private _sideMenuFilters: BehaviorSubject<SearchMap> = new BehaviorSubject(null);
    private _showMainMenu: BehaviorSubject<boolean> = new BehaviorSubject(true);
    private _showSubMenu: BehaviorSubject<boolean> = new BehaviorSubject(false);

    constructor(private dataStoreService: DataStoreService) {
    }

    public mainMenuKey: Observable<string> = this._mainMenuKey.asObservable();
    public subMenuKey: Observable<string> = this._subMenuKey.asObservable();
    public showSideBar: Observable<boolean> = this._showSideBar.asObservable();
    public sideMenuFilters: Observable<SearchMap> = this._sideMenuFilters.asObservable();
    public showMainMenu: Observable<boolean> = this._showMainMenu.asObservable();
    public showSubMenu: Observable<boolean> = this._showSubMenu.asObservable();

    public changeShowSideBar(data: boolean): void {
        this._showSideBar.next(data);        
    }

    public changeSideMenuFilters(data: SearchMap): void {
        this._sideMenuFilters.next(data);
        this.dataStoreService.push('sidebar-search-map', data).then(_ => _);
    }

    public changeShowMainMenu(data: boolean): void {
        this._showMainMenu.next(data);
        this.storeMenuState();
    }

    public changeShowSubMenu(data: boolean): void {
        this._showSubMenu.next(data);
        this.storeMenuState();
    }

    public changeMainMenuKey(data: string): void {
        this._mainMenuKey.next(data);
        this.storeMenuState();
    }

    public changeSubMenuKey(data: string): void {
        this._subMenuKey.next(data);
        this.storeMenuState();
    }

    public getSubMenuKey(): string {
        return this._subMenuKey.getValue();
    }

    public getMainMenuKey(): string {
        return this._mainMenuKey.getValue();
    }

    public getShowSubMenu(): boolean {
        return this._showMainMenu.getValue();
    }

    public getShowMainMenu(): boolean {
        return this._showMainMenu.getValue();
    }

    public storeMenuState(): void {
        const ms = {
            showMainMenu: this._showMainMenu.getValue(),
            showSubMenu: this._showSubMenu.getValue(),
            mainMenuKey: this._mainMenuKey.getValue(),
            subMenuKey: this._subMenuKey.getValue()
        };

        this.dataStoreService.push('menu-state', ms).then(_ => _);
    }

    public getCurrentMenuState(): any {
        return {
            showMainMenu: this._showMainMenu.getValue(),
            showSubMenu: this._showSubMenu.getValue(),
            mainMenuKey: this._mainMenuKey.getValue(),
            subMenuKey: this._subMenuKey.getValue()
        };
    }

    public clearMenuState(): void {
        this.changeSubMenuKey(null);
        this.changeMainMenuKey(null);
        this.changeShowMainMenu(null);
        this.changeShowSubMenu(null);
    }

    public hydrateMenuState(): void {
        this.dataStoreService.get('menu-state').then((s: any) => {
            this.changeMainMenuKey(s.mainMenuKey);
            this.changeSubMenuKey(s.subMenuKey);
            this.changeShowMainMenu(s.showMainMenu);
            this.changeShowSubMenu(s.showSubMenu);
        })
    }

    public hydrateSidebarMenuFilters(): void {
        this.dataStoreService.get('sidebar-search-map').then(f => this.changeSideMenuFilters(f));
    }
}

import { Inject, Injectable } from "@angular/core";
import { Router } from "@angular/router";
import { RouteKeys } from "../constants/route.keys";
import { DataStoreService } from "./data-store.service";
import { HistoryService } from "./history.service";
import { MenuStateService } from "./menu-state.service";
import { SearchStateService } from "./search-state.service";
import { WINDOW } from "./window.service";

@Injectable({
    providedIn: 'root'
})
export class NavigationService {
    constructor(
        private menuStateService: MenuStateService,
        private dataStoreService: DataStoreService,
        private searchStateService: SearchStateService,
        private historyService: HistoryService,
        private router: Router,
        @Inject(WINDOW) private window: any,
    ) {
    }

    public async navigateForCategory(query: any, category: string, currentPage: string, addToHistory: boolean = true): Promise<void> {
        this.searchStateService.clearQuickSearch$.next();
        const data = JSON.parse(query);

        const currentMenuState = this.menuStateService.getCurrentMenuState();
        
        this.menuStateService.changeShowMainMenu(!this.isMobileOrTablet);
        this.menuStateService.changeShowSubMenu(true);
        this.menuStateService.changeMainMenuKey(category);
        this.menuStateService.changeSubMenuKey(null);
        this.menuStateService.changeSideMenuFilters(null);

        await this.dataStoreService.push('sidebar-query', data);

        if (addToHistory === true) {
            await this.historyService.push({ url: currentPage, storeData: [ { Key: 'sidebar-query', Value: data }, { Key: 'menu-state', Value: currentMenuState}] });
        }

        this.router.navigateByUrl(`/${data.lob}/list/${btoa(query)}`, { skipLocationChange: false, replaceUrl: true});
    }

    public async navigateForSubcategory(query: any, subMenuKey: string, mainMenuKey: string = null, addToHistory: boolean = false, currentPage: string = null): Promise<void> {
        this.searchStateService.clearQuickSearch$.next();

        const data = JSON.parse(query);
        const currentMenuState = this.menuStateService.getCurrentMenuState();

        this.menuStateService.changeSubMenuKey(subMenuKey);
        this.menuStateService.changeShowMainMenu(!this.isMobileOrTablet);
        this.menuStateService.changeMainMenuKey((mainMenuKey ? mainMenuKey : this.menuStateService.getMainMenuKey()));
        this.menuStateService.changeShowSubMenu(true);
        this.menuStateService.changeSideMenuFilters(null);

        await this.dataStoreService.push('sidebar-query', data);

        if (addToHistory === true) {
            await this.historyService.push({ url: currentPage, storeData: [ { Key: 'sidebar-query', Value: data }, { Key: 'menu-state', Value: currentMenuState}] });
        }

        this.router.navigateByUrl(`/${data.lob}/list/${btoa(query)}`, { skipLocationChange: false, replaceUrl: true});
    }

    public async navigateForListItem(query: any, currentPage: string, itemId: string): Promise<void> {
        this.searchStateService.clearQuickSearch$.next();

        const data = JSON.parse(query);
        const currentMenuState = this.menuStateService.getCurrentMenuState();

        this.menuStateService.changeSideMenuFilters(null);
        
        await this.historyService.push({ url: currentPage, storeData: [ { Key: 'sidebar-query', Value: data }, { Key: 'menu-state', Value: currentMenuState}] });       

        this.router.navigateByUrl(`/${data.lob}/detail/${itemId}`, { skipLocationChange: false, replaceUrl: true});
    }

    public async navigateHomeClick(): Promise<void> {
        this.searchStateService.clearQuickSearch$.next();

        this.menuStateService.clearMenuState();
        this.menuStateService.changeSideMenuFilters(null);
        await this.historyService.clear();

        this.menuStateService.changeSubMenuKey(null);
        this.menuStateService.changeMainMenuKey(null);
        this.menuStateService.changeShowMainMenu(true);
        this.menuStateService.changeShowSubMenu(false);
        this.router.navigateByUrl(RouteKeys.Home);
    }

    public async navigateForCartClick(query: any, currentPage: string): Promise<void> {
        this.searchStateService.clearQuickSearch$.next();

        const data = JSON.parse(query);
        const currentMenuState = this.menuStateService.getCurrentMenuState();
        
        this.menuStateService.changeShowMainMenu(true);
        this.menuStateService.changeShowSubMenu(false);
        this.menuStateService.changeMainMenuKey(null);
        this.menuStateService.changeSideMenuFilters(null);

        await this.historyService.push({ url: currentPage, storeData: [ { Key: 'sidebar-query', Value: data }, { Key: 'menu-state', Value: currentMenuState}] });

        this.router.navigateByUrl(RouteKeys.ShoppingCart, { skipLocationChange: false, replaceUrl: true});
    }

    public async clear(): Promise<void> {
        this.searchStateService.clearQuickSearch$.next();

        this.menuStateService.clearMenuState();
        this.menuStateService.changeSideMenuFilters(null);

        await this.dataStoreService.push('sidebar-query', null);
        await this.historyService.clear();        
    }

    public async navigateForUrl(
        url: string,
        currentPage: string = '',
        addtoHistory: boolean = false,
        removeFromHistory = false
    ): Promise<void> {
        if (removeFromHistory) {
            this.historyService.pop();
        }
        if (addtoHistory) {
            await this.historyService.push({ url: currentPage });
        }
        this.router.navigateByUrl(url, { skipLocationChange: false, replaceUrl: true});

        await Promise.resolve();
    }

    public resetMenuState(): void {
        this.menuStateService.changeShowMainMenu(true);
        this.menuStateService.changeMainMenuKey(null);
        this.menuStateService.changeSubMenuKey(null);
        this.menuStateService.changeShowSubMenu(false);
    }

    public async back() : Promise<void> {
        const url = await this.historyService.pop();

        await this.router.navigateByUrl(url, { skipLocationChange: false, replaceUrl: true});
    }

    private get isMobileOrTablet(): boolean {
        const mediaQuery = this.window && this.window.matchMedia && this.window.matchMedia('(max-width: 767px)');
        return mediaQuery && mediaQuery.matches;
    }
}

import { ThisReceiver } from "@angular/compiler";
import { Injectable } from "@angular/core";
import { ToastrService } from 'ngx-toastr';
import { DataStoreService } from "./data-store.service";

@Injectable({
    providedIn: 'root'
})
export class NotificationService {
    private readonly defaultTimeout: number = 3000;
    private messages: Array<{key: string, body: string, title: string, type: string, timeout: number }>;
    private loaded: boolean = false;

    constructor (
        private toastr: ToastrService,
        private dataStoreService: DataStoreService
    ) {
    }

    public async showMessage(key: string): Promise<void> {
        await this.ensureMessagesLoaded();        
        const message = this.messages.find(m => m.key.toLowerCase() === key.toLowerCase());        
        if (message) {
            this.toastr.show(
                message.body,
                message.key,
                { timeOut: message.timeout || this.defaultTimeout },
                `toast-${message.type.toLowerCase()}`
            );
        }
    }

    private async ensureMessagesLoaded(): Promise<void> {
        if (!this.loaded) {
            this.messages = await this.dataStoreService.get('notification-messages');
            this.loaded = true;
        }
    }
}

import { Injectable } from "@angular/core";

@Injectable({
    providedIn: 'root'
})
export class OrderFilterService {
    public getFilter(filter: string): { from: number, to: number } {
        const today = new Date(Date.now());
        let dates: any = {};

        switch(filter) {
            case 'last 30 days':
                dates['to'] = new Date(new Date(Date.now()).toDateString());
                dates['from'] = new Date(today.setMonth(today.getMonth() - 1));
                break;
            case 'past 3 months':
                dates = this.getFilterDates(today, 3);
                break;
            case 'past 6 months':
                dates = this.getFilterDates(today, 6);
                break;
            default:
                const d = this.getFilterDates(today, 7);
                dates['to'] = new Date(d.from.getFullYear(), d.from.getMonth(), new Date(d.from.getFullYear(), d.from.getMonth() + 1, 0).getDate());
                break;
        }
        
        return { 
            from: (dates.from ? new Date(dates.from.getFullYear(), dates.from.getMonth(), dates.from.getDate(), 0, 0, 0).getTime() : null),
            to: (dates.to ? new Date(dates.to.getFullYear(), dates.to.getMonth(), dates.to.getDate(), 23, 59, 59).getTime() : null)
        };
    }

    private getFilterDates(today: Date, offset: number): { from: Date, to: Date } {
        const t = new Date(today);
        const f = new Date(today.setMonth(today.getMonth() - (offset - 1)));

        return {
            to: new Date(t.getFullYear(), t.getMonth(), new Date(t.getFullYear(), t.getMonth() + 1, 0).getDate()),
            from: new Date(f.getFullYear(), f.getMonth(), 1)
        };
    }
}

import { HttpClient } from "@angular/common/http";
