@import '../../../../../ui/src/scss/variables';
@import '../../../../../ui/src/scss/mixins';

@mixin signout ($gridColumEnd) {
    grid-column-start: 1;
    grid-column-end: $gridColumEnd;
    width: 100%;
}

.desktop-container {
    display: none;
    
    ui-signout {
        @include signout(7);
    }
}

.mobile-container {
    position: relative;
    background-color: $primary-color;
    height: 100%;
    padding: 5px 0;
    padding-bottom: 10px;
    display: grid;
    grid-auto-flow: row;
    grid-template-columns: 50px calc(100% - 230px) 125px 55px;
    justify-content: center;
    justify-items: center;
    grid-auto-rows: 1fr;

    .search-container {
        grid-column-start: 1;
        grid-column-end: 5;
        width: 100%;
        padding: 2px 20px;
    }

    &.authenticated {
        padding-bottom: 0;
    }
    
    ui-signout {
        @include signout(6);
    }
}

 .company-logo-small {
    position: relative;
    padding-top: 0;
    justify-self: left;

    &:after {
        content: "bazaar";
        position: absolute;
        left: 45px;
        top: 24px;
        color: #fff;
        font-size: 1rem;
    }
}

.menu {
    position: relative;

    i {
        position: absolute;
        left: -15px;
        top: 0;
        font-weight: lighter;
        font-size: 32px;
        border: 1px solid transparent;
        color: $secondary-color;
    }
}

.icon {
    position: relative;

    i.shopping-cart {
        color: $secondary-color;
        font-size: 1.2em;

        &:before {
            content: " ";
            border-top: 3px solid $primary-color;
            left: 9px;
            top: 3px;
            width: 18px;
            height: 6px;
            position: absolute;
            background-color: $primary-color;
        }
    }

    .tq-label {
        position: absolute;
        left: 11px;
        top: -2px;
        font-size: 0.4em;
        font-weight: 700;
        background-color: $quaternary-color;
        width: 18px;
        height: 18px;
        border: 1px solid transparent;
        border-radius: 9px;
        color: white;
        padding: 2px;
    }
}

.user-signin-signout {
    justify-self: end;    
    padding-right: 10px;
    border: 1px solid transparent;
}

ui-burger-menu {
    top: 50px;
    min-width: 300px;
    max-width: 300px;
    left: -320px;
    transition: 1s;
    position: absolute;
    background-color: $hamburger-menu-background-color;
    color: $hamburger-menu-color;
    overflow-x: hidden;
    height: calc(100% - 47px);
    padding-left: 20px;
    z-index: 1;
    min-height: 100vh;
    
    &.animate {
        left: -20px;
    }

    &.back-visible {
        top: 85px;
    }
    
    &.authenticated {
        top: 45px;

        &.back-visible {
            top: 85px;
        }
    }
}

ui-menu {
