    }

    .header-detail-container,
    .header-list-container {
        display: grid;
        width: 100%;
        padding: 11px ;
        column-gap: 5px;
        row-gap: 5px;
        border: $border;
        border-color: transparent;
        border-radius: $border-radius;
        justify-items: var(--header-list-container-justify-items);
        background-color: #f1f1f1;
        position: relative;
    }

    .header-detail-container {
        grid-template-columns: var(--header-detail-container-grid-template-columns);
        grid-auto-flow: var(--header-detail-container-grid-auto-flow);
    }

    .section-container {
        display: grid;
        grid-auto-flow: row;
        grid-template-columns: 1fr;
        margin: 0 0 10px 0;
        box-shadow: 4px 4px 6px #f1f1f1;
        padding: 10px;
        width: 100%;
        border: 1px solid transparent;
        border-radius: $border-radius;
    }

    .header-item {
        font-size: 0.9rem;
        color: #555;
        width: 100%;
        position: relative;
        
        > span:not(:first-child) {
            font-weight: normal;
            font-size: 0.8rem;
            white-space: nowrap;
            margin-left: 10px;
            padding-top: 2px;
        }

        

        &.summary,
        &.shipping,
