            align-content: start;
            row-gap: 5px;

            .summary-item {
                display: grid;
                grid-auto-flow: column;
                justify-content: stretch;
                font-weight: normal;

                span:first-child {
                    text-align: left;
                }

                span:last-child {
                    text-align: right;
                }

                &:last-child {
                    font-weight: 500;
                    margin-top: 10px;
                }
            }
        }

        a {
            position: relative;
            color: #0000ff;
            cursor: pointer;
            font-size: 0.85rem;

            &.down-arrow:after {
                @include down-arrow(-15px, 1px);
            }

            + span {
                margin: 0 10px;
                border-right: 1px inset #c0c0c0;
                margin-top: 3px;
            }

            
        }

        .delivery {
            position: absolute;
            width: 200px;;
            border: $border;
            padding: 11px;
