import { Component, ElementRef, Inject, Input, ViewChild } from "@angular/core";
import { ActivatedRoute, Router } from "@angular/router";
import { 
    BaseComponent,
    ConfigService,
    DataStoreService,
    InfiniteScrollService,
    LightboxService,
    MenuStateService,
    Product,
    ProductDetail,
    ProductReviewService,
    Review,
    ReviewSummary,
    WINDOW
} from "@common/src/public-api";
import { Result } from "@search/src/lib/types";
import { from, interval } from "rxjs";
import { debounce, delay, filter, first, takeUntil } from "rxjs/operators";
import { DetailService } from "../../../services/detail.service";

@Component({
    selector: 'ui-view-review-component',
    templateUrl: './view-review.component.html',
    styleUrls: ['./view-review.component.scss']
})
export class ViewReviewComponent extends BaseComponent {
    private _hasMorePages: boolean = true;
    private _pageNumber: number;
    private _loading: boolean = false;

    metadata: any;
    reviews: Array<Review>;
    product: Product;    
    reviewsCount: number;
    reviewLimit: number;

    @Input() showProductInfo: boolean = false;
    @Input() show: boolean = true;
    @ViewChild('navReviews') navReviewsRef: ElementRef;
    
    constructor(
        router: Router,
        @Inject(WINDOW) window: Window,
        menuStateService: MenuStateService,
        dataStoreService: DataStoreService,
        configService: ConfigService,
        private activatedRoute: ActivatedRoute,
        private detailService: DetailService,
        private infiniteScrollService: InfiniteScrollService,
        private lightboxService: LightboxService,
        private productReviewService: ProductReviewService
    ) {
        super(router, window, menuStateService, dataStoreService);
        this.reviewLimit = configService.getConfiguration().reviewLimit;
    }

    protected async init(): Promise<void> {
        const data = this.activatedRoute.snapshot.data;
        this.activatedRoute.queryParams.pipe(delay(0), first()).subscribe(param  => {
            if (param['vr'] === 'true') {
                this.scrollToReviews();
            }
        });

        this.product = data && data.detail;
        this.reviews = data && data.reviews.items;
        this.reviewsCount = data && data.reviews.count;
        this.metadata = data && data.metadata['ViewReview'];
        this._pageNumber = 1;

        if (this.showProductInfo) {
            this.infiniteScrollService.onScrolledDown
                .pipe(
                    filter(() => this._hasMorePages),
                    debounce(() => interval(200)),
                    takeUntil(this.$destroy))
                .subscribe(() => {
                    if (!this._loading) {
                        this._loading = true;
                        this.productReviewService.getReviews(this.product._id, ++this._pageNumber, this.reviewLimit)
                            .pipe(takeUntil(this.$destroy))
                            .subscribe((data: Result<Review>) => {
                                this._hasMorePages = Array.isArray(data.items) && data.items.length > 0;
                                this.reviews = this.reviews.concat(data.items);
                                this.reviewsCount = data.count;
                                this._loading = false;
                            });
                    }
            });
        }
    }

    get reviewSummary(): ReviewSummary {
        return this.product && this.product.reviewSummary;
    }

    get imageUrl(): string {
        if (this.product && Array.isArray(this.product.images)){
            const image = this.product.images.find(i => Number(i.size) === 0);

            if (image) {
                return image.src;
            }
        }
        return '';
    }

    get sortedFeaturesRating(): Array<any> {
        return this.reviewSummary && this.reviewSummary.featuresRating && this.reviewSummary.featuresRating.sort((a, b) =>  a.value - b.value).reverse();
    }

    get sortedReviews(): Array<any> {
        return this.reviews && this.reviews.sort((a, b) =>  a.modifiedDate - b.modifiedDate).reverse();
    }

    public getReviewDate(review: Review): Date {
        return new Date(new Date(review.modifiedDate).toDateString());
    }

    public isValidDate(review: Review): boolean {
        let dt = new Date(new Date(review.modifiedDate).toDateString());

        return dt instanceof Date && !isNaN(dt.getTime());
    }

    public getSizePack(details: Array<ProductDetail>): string {
        const sizePack = details.find(d => d.title.toLowerCase() === 'sizepack');
        return sizePack?.value ?? '';
    }

    public isSpeciality(title: string): boolean {
        return title.toLowerCase() === 'speciality';
    }

    public getBrand(): string {
        if (this.product.description.toLowerCase().includes(this.product.brand.toLowerCase())){
            return '';
        } else {
            return `${this.product.brand} `;
        }
    }

    public async onItemClick(): Promise<void> {
    }

    public async openLightbox(id: string): Promise<void> {
        this.lightboxService.open(id);

        await Promise.resolve();
    }

    public async onlightboxClose(id: string): Promise<void> {
        this.lightboxService.close(id);

        await Promise.resolve();
    }

    public async onSeeMoreReviewsClick(): Promise<void> {
        
    }

    public getRating(r: any): string {
        return `--rating: ${r && r.rating || 0}`;
    }

    public getLightboxData(src: string, idx: string): any {
        return { src, id: `review-image-${idx}` };
    }

    public async onVoteChange(review: Review, vote: { likeVote: boolean, dislikeVote: boolean }): Promise<void> {
        try {
            await this.productReviewService.updateReviewVote(review._id, vote).toPromise();
            
            if (!review.votes) {
                review.votes = { likeVote: 0, dislikeVote: 0 };
            }

            if (vote.likeVote) {
                review.votes.likeVote++;
            }
    
            if (vote.dislikeVote) {
                review.votes.dislikeVote++;
            }

        } catch (e) {
            // TODO: Error handling
        }
    }

    private scrollToReviews(): void {        
        this.navReviewsRef.nativeElement.scrollIntoView({ behavior: 'smooth' });
    }
}

styleUrlsData.data:
@import '../../../../../../ui//src/scss/variables';
@import '../../../../../../ui//src/scss/mixins';
@import '../mixins.scss';

.container {
    @include non-content-container;
    color: $desktop-regular-color;
    padding: 0 10px;

    .product-review-container {
        display: grid;
        grid-auto-flow: row;
        grid-template-columns: 1fr;
        margin-top: 20px;

        &.no-product {
            grid-template-columns: 1fr;
        }

        .review-summary {
            .header {
                @include header;
            }
    
            .no-product {
                display: grid;
                grid-template-columns: 1fr;
                justify-items: stretch;

                ui-feature-review-component {
                    .container {
                        ::ng-deep & {
                            margin-top: 9px;
                        }

                        .header {
                            ::ng-deep & {
                                margin-bottom: 16px;
                            }
                        }
                    }
                }
            }
            
            ui-feature-review-component,
            ui-review-summary-component {
                max-width: 280px;

                .container {
                    ::ng-deep & {
                        padding: 11px 0;
                    }
                }
            }
        }
    
        .product-summary {
            display: grid;
            grid-auto-flow: row;
            grid-template-columns: 1fr 2fr;
            column-gap: 10px;
            margin-top: 14px;
    
            figure {
                margin: 0;
                cursor: pointer;
                text-align: center;
                height: 180px;
                margin-bottom: 10px;
        
                img {
                    max-width: 150px;
                    max-height: 100%;
                }
            }
    
            .summary {
                display: grid;
                grid-auto-flow: row;
                justify-content: left;
                align-content: start;
                row-gap: 5px;
    
                .description {
                    font-weight: 600;
                    font-size: 1.2rem;
                }
    
                .size {
                    font-size: $font-size;
                    font-weight: normal;
                    line-height: 20px;
                    color: $mobile-regular-color;
                    max-width: 280px;
                    margin-bottom: 5px;
                }
    
                .size {
                    
                }
    
                .country-container {
                    font-size: $font-size;
                    margin-top: 5px;
                    color: $desktop-regular-color;
    
                    .country {
                        font-size: 1.1em;
                        color: $desktop-regular-color;
                        font-weight: 500;
                    }
                }
    
                .speciality {
                    font-weight: normal;
                    font-size: 0.8em;
                    margin-top: 5px;
                    
                    .veg-symbol {
                        color: green;
                        font-weight: 600;
                    }
    
                    .pre-text {
                        color: $desktop-regular-color;
                        margin-left: 5px;
                    }
    
                    .text {
                        color: $desktop-regular-color;
                        font-weight: 500;
                        line-height: 10px;
                    }
                }
            }
    
            ui-add-to-shoppingcart-quantity {
                
                .add-to-basket-container {
                    ::ng-deep & {
                        padding: 0;
                    }
                }
            }
    
        }
    }

    .reviews-container {
        position: relative;

        .review-user {
            @include reviewer;
        }

        .feature-rating {
            margin-top: 10px;

            .header {
                margin-left: 20px;
                font-weight: 500;
                font-size: 1.2rem;
            }

            .no-reviews {
                cursor: default;
            }
        }

        .images-container {
            display: grid;
            grid-auto-flow: column;
            justify-content: stretch;
            margin-top: 10px;

            figure {
                @include small-product-image(50px, 50px, false);

                cursor: default;
                border: 1px solid #c0c0c0;
                padding: 1px;
                box-sizing: content-box;

                &:not(:first-child) {
                    margin-left: 2px;
                }
            }

            ui-carousel-component {
                width: calc(100vw - 20px);

                .carousel-wrapper {
                    ::ng-deep & {
                        display: grid;
                        grid-auto-flow: column;
                        grid-template-columns: 20px auto 20px;
                        column-gap: 5px;
                    }

                    i {
                        ::ng-deep & {
                            position: relative;
                            top: 8px;
                            transform: unset;
                            opacity: 1;
                        }

                        &.chevron-right {
                            ::ng-deep & {
                                right: 9px;
                            }
                        }
                    }
                }
            }
        }
        .description {
            margin-top: 5px;
            padding: 2px 0;
            line-height: 1.3rem;
        }

        .review-date {
            font-size: 0.9rem;
            margin-top: 5px;
        }

        .nav-reviews {
            position: absolute;
            left: 1px;
            top: 1px;
        }
    }  

    .review-note {
        font-size: 0.9rem;
        font-weight: 600;
        margin-top: 20px;
    }

    .header {
        @include header;
    }

    .mt-20 {
        margin-top: 20px;
    }

    .mt-40 {
        margin-top: 40px;
    }

    .see-more-reviews {
        a {
            color: #0000ff;

            &:hover {
                text-decoration: underline;
            }
        }
    }
}

ui-lightbox-component {
    .lightbox {
        .lightbox-body {    
            figure {
                ::ng-deep & {
                    margin-top: 20px;
                }
            }
        }
    }        
}

@media only screen and (min-width: $tablet-min-width) {
    .container {    
        .product-review-container {
            grid-template-columns: 1fr 1fr;

            .review-summary {
                .no-product {
                    grid-template-columns: 1fr 1fr;
                    column-gap: 40px;
                }

                ui-feature-review-component,
                ui-review-summary-component {
                    max-width: unset;
                }
            }
        }

        .reviews-container {
            .images-container {
                ui-carousel-component {
                    width: calc(100% - 20px);
                }
            }
        }
    }

    ui-lightbox-component {
        .lightbox {
            .lightbox-body {
                ::ng-deep & {
                    position: absolute;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%);
                    text-align: right;
                }

                button {
                    ::ng-deep & {
                        width: 200px;
                    }
                }
        
                figure {
                    ::ng-deep & {
                        margin-top: 20px;
                        width: 400px;
                    }
                    
                    img {
                        ::ng-deep & {
                            width: 400px;
                        }
                    }
                }
            }
        }        
    }
}

